	//Own pattern for checking a email string
	private static final String OWN_PATTERN = "^[_A-Za-z0-9-\\+]+(\\.[_A-Za-z0-9-]+)*@[A-Za-z0-9]+(\\.[A-Za-z0-9]+)*(\\.[A-Za-z]{2,})*$";

//	^						#start of the line
//	  [_A-Za-z0-9-\\+]+		#  must start with string in the bracket [ ], must contains one or more (+), _ , A-Z , a-z , 0-9 , 
//	  (						#   start of group #1, look at definition down
//	    \\.[_A-Za-z0-9-]+	#     follow by a dot "." and string in the bracket [ ], must contains one or more (+) - match more times
//	  )*					#   end of group #1 -  match one or more times, this group is optional (*)
//	    @					#     must contains a "@" symbol
//	    [A-Za-z0-9]+      	#       follow by string in the bracket [ ], must contains one or more (+)
//	      (					#         start of group #2 - first level TLD checking
//	       \\.[A-Za-z0-9]+  #           follow by a dot "." and string in the bracket [ ], must contains one or more (+)
//	      )*				#         end of group #2, this group is optional (*)
//	      (					#         start of group #3 
//	       \\.[A-Za-z]{2,}  #           follow by a dot "." and string in the bracket [ ], with minimum length of 2
//	      )*				#         end of group #3, this group is optional (*)
//	$						#end of the line
	
	@Override
	public boolean isValid(String s) {
		Pattern pattern = Pattern.compile(OWN_PATTERN);
		Matcher matcher = pattern.matcher(s);	
		return matcher.matches();
	}
